import React, { useState } from 'react';
import axios from 'axios';
import './Login.css';  // Include the custom styles

const Login = ({ onLogin }) => {
  const [licenseKey, setLicenseKey] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      // Send a request to the server to verify the license key
      const response = await axios.post('/api/login', { licenseKey });

      if (response.data.valid) {
        onLogin(response.data.userType); // Pass userType (admin/user)
      } else {
        setErrorMessage('Invalid License Key');
      }
    } catch (error) {
      setErrorMessage('Error during login. Please try again.');
    }
  };

  return (
    <div className="login-container">
      <img src="/styles/favicon.ico" alt="Ragnarok Logo" className="logo" />
      <h1>Ragnarok</h1>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Enter License Key"
          value={licenseKey}
          onChange={(e) => setLicenseKey(e.target.value)}
          className="license-input"
        />
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        <button type="submit" className="login-btn">Login</button>
      </form>
    </div>
  );
};

export default Login;
